/**********************************************************************************************************************/
/** \addtogroup Common
 * @{
 **********************************************************************************************************************/

/**********************************************************************************************************************/
/** This file contains functions declaration used for usual byte operations
 ***********************************************************************************************************************
 *
 * @file
 *
 **********************************************************************************************************************/

#ifndef __BYTE_H__
#define __BYTE_H__

/**********************************************************************************************************************/
/** The HiByte function returns the high byte of the value
 ***********************************************************************************************************************
 *
 * @param u16Value
 *
 **********************************************************************************************************************/
uint8_t Byte_HiByte(
  uint16_t u16Value
  );

/**********************************************************************************************************************/
/** The LoByte function returns the low byte of the value
 ***********************************************************************************************************************
 *
 * @param u16Value
 *
 **********************************************************************************************************************/
uint8_t Byte_LoByte(
  uint16_t u16Value
  );

/**********************************************************************************************************************/
/** The HiWord function returns the high word of the value
 ***********************************************************************************************************************
 *
 * @param u32Value
 *
 **********************************************************************************************************************/
uint16_t Byte_HiWord(
  uint32_t u32Value
  );

/**********************************************************************************************************************/
/** The LoWord function returns the low word of the value
 ***********************************************************************************************************************
 *
 * @param u32Value
 *
 **********************************************************************************************************************/
uint16_t Byte_LoWord(
  uint32_t u32Value
  );

/**********************************************************************************************************************/
/**
 ***********************************************************************************************************************
 *
 * @param
 *
 **********************************************************************************************************************/
uint16_t Byte_MakeWord(
  uint8_t u8HiByte,
  uint8_t u8LoByte
  );

/**********************************************************************************************************************/
/**
 ***********************************************************************************************************************
 *
 * @param
 *
 **********************************************************************************************************************/
uint32_t Byte_MakeDoubleWord(
  uint8_t u8HiByte0,
  uint8_t u8Byte1,
  uint8_t u8Byte2,
  uint8_t u8LoByte3
  );

/**********************************************************************************************************************/
/** Converts uint16 from network to host byte order
 ***********************************************************************************************************************
 *
 * @param
 *
 **********************************************************************************************************************/
uint16_t Byte_N2Hs(
  uint16_t u16NetworkValue
  );

#endif

/**********************************************************************************************************************/
/** @}
 **********************************************************************************************************************/
