#include <stdbool.h>
#include <stdint.h>
#include <Byte.h>

/**********************************************************************************************************************/
/** The HiByte function returns the high byte of the value
 ***********************************************************************************************************************
 *
 **********************************************************************************************************************/
uint8_t Byte_HiByte(
  uint16_t u16Value
  )
{
  return (uint8_t) ((u16Value >> 8) & 0x00FF);
}

/**********************************************************************************************************************/
/** The LoByte function returns the low byte of the value
 ***********************************************************************************************************************
 *
 **********************************************************************************************************************/
uint8_t Byte_LoByte(
  uint16_t u16Value
  )
{
  return (uint8_t) (u16Value & 0x00FF);
}

/**********************************************************************************************************************/
/** The HiWord function returns the high word of the value
 ***********************************************************************************************************************
 *
 **********************************************************************************************************************/
uint16_t Byte_HiWord(
  uint32_t u32Value
  )
{
  return (uint16_t) ((u32Value & 0xFFFF0000) >> 16);
}

/**********************************************************************************************************************/
/** The LoWord function returns the low word of the value
 ***********************************************************************************************************************
 *
 **********************************************************************************************************************/
uint16_t Byte_LoWord(
  uint32_t u32Value
  )
{
  return (uint16_t) (u32Value & 0x0000FFFF);
}

/**********************************************************************************************************************/
/**
 ***********************************************************************************************************************
 *
 **********************************************************************************************************************/
uint16_t Byte_MakeWord(
  uint8_t u8HiByte,
  uint8_t u8LoByte
  )
{
  return (u8HiByte << 8) | u8LoByte;
}

/**********************************************************************************************************************/
/**
 ***********************************************************************************************************************
 *
 **********************************************************************************************************************/
uint32_t Byte_MakeDoubleWord(
  uint8_t u8HiByte0,
  uint8_t u8Byte1,
  uint8_t u8Byte2,
  uint8_t u8LoByte3
  )
{
  return (u8HiByte0 << 24) | (u8Byte1 << 16) | (u8Byte2 << 8) | u8LoByte3;
}

/**********************************************************************************************************************/
/** Converts uint16 from network to host byte order
 **********************************************************************************************************************/
uint16_t Byte_N2Hs(
  uint16_t u16NetworkValue
  )
{
  uint8_t u8ByteHi = Byte_HiByte(u16NetworkValue);
  uint8_t u8ByteLo = Byte_LoByte(u16NetworkValue);

  // return value reversing bytes
  return Byte_MakeWord(u8ByteLo, u8ByteHi);
}
